unsigned long long pows[] = {
    1ULL,
    10ULL,
    100ULL,
    1000ULL,
    10000ULL,
    100000ULL,
    1000000ULL, 
    10000000ULL,
    100000000ULL,
    1000000000ULL,
    10000000000ULL,
    100000000000ULL,
};



unsigned long long stat (unsigned long long n)
{
    unsigned long long res = 0;

    while (n) {
        res += pows[n % 10];
        n /= 10;
    }

    return res;
}


int brute (unsigned long long from, unsigned long long to)
{
    int res = 0;

    while (from <= to) {
        if (stat (from) == stat (from << 1))
            res++;
        from++;
    }

    return res;
}


int main (int argc, char* argv[])
{
    int t;
    unsigned long long size, v1, v2, max;
    int g1, g2, res, i;

    max = 5000000000LLU;
    size = max / GROUP_SIZE;

    scanf ("%d", &t);

    while (t--) {
        scanf ("%llu %llu", &v1, &v2);

        g1 = v1 / size;
        g2 = v2 / size;

        if (g1 == g2)
            res = brute (v1, v2);
        else {
            res = brute (v1, size * (g1+1));
            for (i = g1+1; i < g2; i++)
                res += groups[i];
            res += brute (size * g2, v2);
        }

        printf ("%d\n", res);
    }

    return 0;
}
